import React, { useState } from "react";
import Header from "../../components/Header";
import Axios from "axios";

function AddNewBlog() {
  const url = "http://localhost:8080/LoopBlog";
  const [data, setData] = useState({
    title: "",
    paragraph: "",
  });

  function handle(e: any) {
    const newData: any = { ...data };
    newData[e.target.id] = e.target.value;
    console.log(newData);
  }

  function submit(e: any) {
    e.preventDefault();
    Axios.post(url, {
      title: data.title,
      paragraph: data.paragraph,
    }).then((res) => {
      console.log(res.data);
    });
  }

  return (
    <>
      <div>
        <Header />
      </div>
      <div className="mt-48">
        <div className="flex flex-col items-center sm:text-4xl">
          <form>
            <label className="block mb-1 font-light text-gray-500">Title</label>
            <input
              className="w-full border-2 border-gray-200 p-3 rounded outline-none focus:border-black-500"
              type="text"
              //onChange={(e) => handle(e)}
              id="title"
              value={data.title}
            ></input>
            <input
              onChange={(e) => handle(e)}
              id="paragraph"
              value={data.paragraph}
              placeholder="paragraph"
              type="text"
              className="text-black"
            ></input>
          </form>
        </div>
      </div>
    </>
  );
}

export default AddNewBlog;
