import React, { useState, useEffect } from "react";
import axios from "axios";
import { response } from "express";
import Loop from "../../assets/loop.gif";

const Body = () => {
  const url = "http://localhost:8080/tshirt";
  const [shirts, setShirt] = useState("");
  const [size, setSize] = useState("");

  useEffect(() => {
    axios
      .get(url)
      .then((res) => {
        console.log(res.data.tshirt);
        setShirt(res.data.tshirt);
        console.log(res.data.size);
        setSize(res.data.size);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [url]);

  return (
    <div>
      <img src={Loop} width="1050" />
      {/* <h1>{shirts} </h1>
      <h2>{size} </h2> */}
      <div className="ml-24 my-10 flex justify-end">
        <div
          className="font-light text-2xl  flex items-end 
    text-gray-800"
        >
          Welcome to
        </div>
        <div
          className="font-light text-4xl  flex items-center mt-4
    text-gray-800"
        >
          The Loop
        </div>
      </div>
    </div>
  );
};

export default Body;
